import { useDispatch } from 'react-redux';
import { addGoods } from '../action';

const Goods = () => {
    const dispatch = useDispatch();
    const formHandler = (event) => {
        event.preventDefault();
        console.log(event.target.elements);
        let data = event.target.elements;
        dispatch(addGoods(data.id.value, data.title.value, data.image.value));
    }

    return (
        <div>
            <form onSubmit={formHandler}>
                <div>
                    <input type="text" name='id' defaultValue='28hu79' />
                </div>
                <div>
                    <input type="text" name='title' defaultValue='Стирка Whirl' />
                </div>
                <div>
                    <input type="text" name='image' defaultValue='https://con.ru' />
                </div>
                <button type="submit">Add new goods</button>
            </form>
        </div>
    );
}

export default Goods;
