import './App.css';
import React, { useState, useEffect } from 'react';



function App() {

  const [T1, setT1] = useState('');
  const [T2, setT2] = useState('');
  const [T3, setT3] = useState('');
  const [T4, setT4] = useState('');
  const [T5, setT5] = useState('');

  function task1() {
    fetch("http://test1.ru/api.php", {
      method: 'POST',
      header: {
        'Content-type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify({ action: 1 })
    })
      .then(response => response.text())
      .then(response => {
        setT1(response);
      });
  }

  function task2(event) {

    event.preventDefault();

    let data = event.target.elements;
    let num1_val = data.num1.value;
    let num2_val = data.num2.value;

    fetch('http://test1.ru/api.php', {
      method: 'POST',
      header: {
        'content-type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify({
        action: 2,
        num1: num1_val,
        num2: num2_val
      })
    })
      .then(response => response.text())
      .then(response => {
        setT2(response);
      })
  }

  function task3(event) {

    event.preventDefault();

    let data = event.target.elements;

    let file_Name = data.filename.value;
    let file_Data = data.filedata.value;

    fetch('http://test1.ru/api.php', {
      method: 'POST',
      header: {
        'content-type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify({
        action: 3,
        filename: file_Name,
        filedata: file_Data,
      })
    })
      .then(response => response.text())
      .then(response => {
        if (response > 0) {
          setT3(file_Name);
        }
        else {
          setT3(false);
        }
      });

  }

  function task4(event) {
    event.preventDefault();

    fetch('http://test1.ru/api.php', {
      method: 'POST',
      header: {
        'content-type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify({ action: 4 })
    })
      .then(response => response.stringify())
      .then(response => {
        console.log(response);
      })
  }

  function task5(event) {
    event.preventDefault();

    fetch('http://test1.ru/api.php', {
      method: 'POST',
      header: {
        'content-type': 'application/x-www-form-urlencoded',
      },
      body: JSON.stringify({ action: 5 })
    })
    .then(response => response)
    .then(response => {
      console.log(response)
    })
  }

  return (
    <div>
      <h1>ItGid.info</h1>
      {/* <p>{text}</p> */}
      <hr />
      <div>
        <h2>Время сервера</h2>
        <button onClick={task1}>GET</button>
        <p>{T1}</p>
      </div>
      <hr />
      <div>
        <h2>Случайное число между</h2>
        <form action="" onSubmit={task2}>
          <div><input type="number" name="num1" defaultValue="30" /></div>
          <div><input type="number" name="num2" defaultValue="44" /></div>
          <button type="sumbit">Push</button>
        </form>
        <p>{T2}</p>
      </div>
      <hr />
      <div>
        <h2>Создание файла</h2>
        <form action="" onSubmit={task3}>
          <div><input type="text" name="filename" /></div>
          <div><input type="text" name="filedata" /></div>
          <button type="sumbit">Push</button>
        </form>
        <p>{ }</p>
      </div>
      <hr />
      <div>
        <h2>Получение данных компьютера</h2>
        <form action="" onSubmit={task4}>
          <button type="sumbit">Push</button>
        </form>
        <p>{T4}</p>
        <hr />
        <div>
          <h2>Получение курса валют</h2>
          <form action="" onSubmit={task5}>
            <button type="sumbit">Push</button>
          </form>
          <ul></ul>
        </div>
      </div>
    </div>
  );
}


export default App;
