import { createSlice } from "@reduxjs/toolkit";

export const counterSlice = createSlice({
    name: 'counter',
    initialState: {
        value: 0,
        text : 'privet'
    },

    reducers: {
        inccrement: state => {
            state.value += 15;
        },
        showConsole : state => {}
    }
})
